mapper.insert:
insert into tip(
	user, title, content, category, hits
) values (
	#{user}, #{title}, #{content}, #{category}, 0
)

mapper.update:
update tip 
set title = #{title}, 
	content = #{content}, 
	category = #{category}
where id = #{id}

mapper.select:
select id, user, title, content, date, hits, category
from tip
where id = #{id}
