mapper.update:
UPDATE shop
SET name=#{name}, address=#{address}, phone=#{phone}, number=#{number}, description=#{description}, imgDesc=#{imgDesc}, img=#{img}, region=#{region}
WHERE id=#{id}

UPDATE shop
SET img=#{img}
WHERE id=#{id}

UPDATE shop
SET imgDesc=#{imgDesc}
WHERE id=#{id}

mapper.select:
select name
from shop
where id = #{id}
