mapper.insert:
insert into user(
    email, name, ukey
) values (
    #{email}, #{name}, #{ukey}
)

mapper.select:
select id, email, name, domain, ukey
from user
where id = #{id}

select id, email, name, domain, ukey
from user
where name = #{name}

select id, email, name, domain, ukey
from user
where email = #{email}

select id, email, name, domain, ukey
from user
where ukey = #{ukey}

mapper.update:
update user
set email = #{email}
    name = #{name}
where id = #{id}

mapper.select:
SELECT code
FROM user_authority
WHERE user=#{user}
