mapper.insert:
insert into user(
    email, name, key
) values (
    #{email}, #{name}, #{key}
)

mapper.select:
select id, email, name, domain, key
from user
where id = #{id}

select id, email, name, domain, key
from user
where name = #{name}

select id, email, name, domain, key
from user
where email = #{email}

select id, email, name, domain, key
from user
where key = #{key}

mapper.update:
update user
set email = #{email}
    name = #{name}
where id = #{id}
